mapper.update:
UPDATE tb_resume
SET name = #{name},gender = #{gender},age = #{age},education = #{education},school = #{school},job_intention = #{jobIntention},phone = #{phone},email = #{email}
WHERE user_id = #{userId}

mapper.select:
SELECT job_id AS jobId,etp_id AS etpId,message
FROM tb_interview
WHERE user_id = #{userId} AND interview_status = #{interviewStatus}

SELECT name,description,salary_desc AS salaryDesc,location,requirement,id,etp_id AS etpId FROM tb_job WHERE id in
#{jobId}
(NULL)

SELECT id,logo_file_id AS logoFileId,name FROM tb_etp WHERE id in
#{etpId}
(NULL)

SELECT attachment_file_ids As attachmentFileIds
FROM tb_resume
WHERE user_id = #{userId}
